import { makeStyles } from '@material-ui/styles';


const useStyles =  makeStyles(() => ({

    root : {
        maxWidth : '100%'
    },
    media : {
        height : '0',
        paddingTop : '56.25%'
    },
    cardActions : {
        display: 'flex',
        justifyContent : 'space-between'
    },
    cardContent : {
        display: 'flex',
        justifyContent : 'space-between'
    },

}))

export default useStyles
